Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_LEFT");
GameTooltip:AddLine("ArcEmu GM Addon");
GameTooltip:AddLine("Left click to show/hide");
GameTooltip:AddLine("Right click to drag this");
GameTooltip:Show();

Scripts.OnClick:
			if (GMAddonForm:IsShown()) then
				GMAddonForm:Hide();
			elseif (AnnounceForm:IsShown()) then
				AnnounceForm:Hide();    
			elseif (BanForm:IsShown()) then
				BanForm:Hide();	
			elseif (BGForm:IsShown()) then
				BGForm:Hide();
            elseif (CheatingForm:IsShown()) then
                CheatingForm:Hide();
			elseif (CommForm:IsShown()) then
				CommForm:Hide();
			elseif (ItemForm:IsShown()) then
				ItemForm:Hide();
			elseif (Mini:IsShown()) then
				Mini:Hide();
			elseif (MiscForm:IsShown()) then
				MiscForm:Hide();
            elseif (ModifyForm:IsShown()) then
                ModifyForm:Hide();
            elseif (ModifyFormHelp:IsShown()) then
                ModifyFormHelp:Hide();
                ModifyForm:Hide();
			elseif (NPCForm:IsShown()) then
				NPCForm:Hide();
			elseif (ObjectForm:IsShown()) then
				ObjectForm:Hide();
			elseif (PlayerForm:IsShown()) then
				PlayerForm:Hide();
			elseif (ProfessionsForm:IsShown()) then
				ProfessionsForm:Hide();
			elseif (RidingForm:IsShown()) then
				RidingForm:Hide();
			elseif (SkillForm:IsShown()) then
				SkillForm:Hide();
			elseif (SpellForm:IsShown()) then
				SpellForm:Hide();
			elseif (TeleForm:IsShown()) then
				TeleForm:Hide();
			elseif (TicketForm:IsShown()) then
				TicketForm:Hide();
			elseif (WaypointForm:IsShown()) then
				WaypointForm:Hide();
			elseif (WRForm:IsShown()) then
				WRForm:Show();
			else
				GMAddonForm:Show();
			end
